import 'package:flutter/material.dart';
import 'package:task_manager_flutter/data/utils/api_links.dart';
import 'package:task_manager_flutter/ui/widgets/user_banners.dart';
import 'package:task_manager_flutter/ui/screens/update_profile.dart';
import 'package:task_manager_flutter/ui/widgets/home_list_model.dart';
import 'package:task_manager_flutter/data/models/network_response.dart';
import 'package:task_manager_flutter/data/services/network_caller.dart';
import 'package:task_manager_flutter/ui/widgets/input_field_busca.dart';
import 'package:task_manager_flutter/ui/screens/exames_add.dart';
import 'package:task_manager_flutter/ui/screens/exames_list.dart';
import 'package:task_manager_flutter/data/models/auth_utility.dart';
import '../../data/models/login_model.dart';
import 'package:flutter/material.dart';
import 'package:fl_chart/fl_chart.dart';

class CotacaoScreen extends StatefulWidget {
  @override
  _CotacaoScreenState createState() => _CotacaoScreenState();
}

class _CotacaoScreenState extends State<CotacaoScreen> {
  List<CotacaoDTO> cotacoes = [];
  bool isLoading = true;

  @override
  void initState() {
    super.initState();
    fetchCotacoes().then((data) {
      setState(() {
        cotacoes = data;
        isLoading = false;
      });
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Cotações'),
      ),
      body: isLoading
          ? Center(child: CircularProgressIndicator())
          : Padding(
              padding: const EdgeInsets.all(16.0),
              child: Column(
                children: [
                  Text(
                    'Cotações de Ações',
                    style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
                  ),
                  SizedBox(height: 20),
                  Expanded(
                    child: LineChart(
                      LineChartData(
                        gridData: FlGridData(show: false),
                        titlesData: FlTitlesData(
                          leftTitles: SideTitles(showTitles: true),
                          bottomTitles: SideTitles(showTitles: true),
                        ),
                        borderData: FlBorderData(show: true),
                        minX: 0,
                        maxX: cotacoes.length.toDouble() - 1,
                        minY: 0,
                        maxY: cotacoes.map((c) => c.valor!).reduce((a, b) => a > b ? a : b),
                        lineBarsData: [
                          LineChartBarData(
                            spots: cotacoes.asMap().entries.map((entry) {
                              int index = entry.key;
                              CotacaoDTO cotacao = entry.value;
                              return FlSpot(index.toDouble(), cotacao.valor!);
                            }).toList(),
                            isCurved: true,
                            colors: [Colors.blue],
                            dotData: FlDotData(show: true),
                            belowBarData: BarAreaData(show: false),
                          ),
                        ],
                      ),
                    ),
                  ),
                ],
              ),
            ),
    );
  }
}
