+    void addData() {
+      Map<String, dynamic> json = {
+        'id': key,
+        chave: value,
+        chave: value,
+        chave: value,
+        chave: value
+      };
+      _dataArray.add(json);
+      setState(() {
+        _data = _dataArray.toString();
+      });
+    }
+
+    if (_dataArray.isEmpty) {
+      addData();
+    } else {
+      _dataArray.asMap().entries.map((entry) {
+        if (entry.key == key && entry.value == chave) {
+          _dataArray[key][chave] = value;
+        }
+        print(entry.key);
+        print(entry.value);
+      });
+
+      for (var map in _dataArray) {
+        if (map["id"] == key) {
+          _dataArray[key][chave] = value;
+          setState(() {
+            _data = _dataArray.toString();
+          });
+          break;
+        }
+      }
+
+      for (var map in _dataArray) {
+        if (map["id"] == key) {
+          return;
+        }
+      }
+      addData();
+    }
+  }
